mapper.select:
select * from project_bidding


        and id = #{id}


        and project_id = #{projectId}

select b.id,b.project_id,b.template_id,b.package_id,b.type,b.stage,b.name,b.file_type,b.download_url,project_code,filings_number,project_name,procure_type,contract_package,package_num,bidding_subject,b.create_by,b.create_time,b.update_by,b.update_time from project_bidding b
    INNER JOIN project_message on project_id = project_message.id INNER JOIN package_information on package_id = package_information.id


        and id = #{id}


        and project_id = #{projectId}


        and template_id = #{templateId}


        and package_id = #{packageId}


        and type = #{type}


        and stage = #{stage}


        and name = #{name}


        and file_type = #{fileType}


        and download_url = #{downloadUrl}
